bi_model_2 <- glm(binomial ~ Lat + ymd_hms_numeric, data = ff125,
                       family = binomial(link = "logit"))

lat <- seq(64.131518, 64.37879, .001)
time <- seq(1596199560, 1598449320, 6436289)


pred_2 <- predict(object = bi_model_2, newdata = data.frame(Lat = lat, ymd_hms_numeric = time), 
                  type = "response")

pred.data.2 <- data.frame(cbind(lat, time, pred_2))        
pred.data.2

ggplot(pred.data.2, aes(x = time, y = lat)) +
  geom_raster(aes(fill = pred_2))

bi_model_3 <- glm(binomial ~ Lat + Long, data = ff125,
                  family = binomial(link = "logit"))

long <- rep(-161.5, 248)

pred_3 <- predict(bi_model_3, data.frame(Lat = lat, Long = long),
                  type = "response")
pred_3
pred.data.3 <- data.frame(cbind(lat, long, pred_3))
pred.data.3


